contracts/ShipmentTracking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title ShipmentTracking
 * @dev Smart contract for recording and verifying shipment events on blockchain
 * @notice Provides immutable tracking of cargo movements and status updates
 */
contract ShipmentTracking {

    // Struct for Shipment Event
    struct ShipmentEvent {
        string eventType;        // e.g., "Loaded", "Departed", "In Transit", "Arrived"
        string location;
        string description;
        uint256 timestamp;
        address recordedBy;
        bool verified;
    }

    // Struct for Shipment
    struct Shipment {
        string shipmentId;
        string blNumber;         // Reference to Bill of Lading
        string containerNumber;
        address shipper;
        address carrier;
        uint256 createdAt;
        ShipmentStatus status;
        bool exists;
    }

    // Enum for Shipment Status
    enum ShipmentStatus {
        Created,
        Loaded,
        InTransit,
        AtPort,
        CustomsClearance,
        OutForDelivery,
        Delivered
    }

    // Mapping from shipment ID to Shipment
    mapping(string => Shipment) private shipments;

    // Mapping from shipment ID to array of events
    mapping(string => ShipmentEvent[]) private shipmentEvents;

    // Mapping to track authorized recorders for each shipment
    mapping(string => mapping(address => bool)) private authorizedRecorders;

    // Array of all shipment IDs
    string[] private shipmentIds;

    // Events
    event ShipmentCreated(
        string indexed shipmentId,
        string blNumber,
        address indexed shipper,
        address indexed carrier,
        uint256 timestamp
    );

    event EventRecorded(
        string indexed shipmentId,
        string eventType,
        string location,
        address indexed recordedBy,
        uint256 timestamp
    );

    event StatusUpdated(
        string indexed shipmentId,
        ShipmentStatus newStatus,
        uint256 timestamp
    );

    event RecorderAuthorized(
        string indexed shipmentId,
        address indexed recorder,
        uint256 timestamp
    );

    event EventVerified(
        string indexed shipmentId,
        uint256 eventIndex,
        address indexed verifier,
        uint256 timestamp
    );

    // Modifiers
    modifier onlyShipper(string memory _shipmentId) {
        require(
            shipments[_shipmentId].shipper == msg.sender,
            "Only shipper can perform this action"
        );
        _;
    }

    modifier onlyAuthorizedRecorder(string memory _shipmentId) {
        require(
            authorizedRecorders[_shipmentId][msg.sender] ||
            shipments[_shipmentId].shipper == msg.sender ||
            shipments[_shipmentId].carrier == msg.sender,
            "Not authorized to record events"
        );
        _;
    }

    modifier shipmentExists(string memory _shipmentId) {
        require(shipments[_shipmentId].exists, "Shipment does not exist");
        _;
    }

    /**
     * @dev Create a new shipment
     * @param _shipmentId Unique shipment identifier
     * @param _blNumber Associated Bill of Lading number
     * @param _containerNumber Container number
     * @param _carrier Address of the carrier
     */
    function createShipment(
        string memory _shipmentId,
        string memory _blNumber,
        string memory _containerNumber,
        address _carrier
    ) public {
        require(!shipments[_shipmentId].exists, "Shipment already exists");
        require(_carrier != address(0), "Invalid carrier address");

        Shipment memory newShipment = Shipment({
            shipmentId: _shipmentId,
            blNumber: _blNumber,
            containerNumber: _containerNumber,
            shipper: msg.sender,
            carrier: _carrier,
            createdAt: block.timestamp,
            status: ShipmentStatus.Created,
            exists: true
        });

        shipments[_shipmentId] = newShipment;
        shipmentIds.push(_shipmentId);

        // Authorize shipper and carrier
        authorizedRecorders[_shipmentId][msg.sender] = true;
        authorizedRecorders[_shipmentId][_carrier] = true;

        emit ShipmentCreated(
            _shipmentId,
            _blNumber,
            msg.sender,
            _carrier,
            block.timestamp
        );

        emit RecorderAuthorized(_shipmentId, msg.sender, block.timestamp);
        emit RecorderAuthorized(_shipmentId, _carrier, block.timestamp);
    }

    /**
     * @dev Record a new shipment event
     * @param _shipmentId Shipment identifier
     * @param _eventType Type of event
     * @param _location Location of event
     * @param _description Event description
     */
    function recordEvent(
        string memory _shipmentId,
        string memory _eventType,
        string memory _location,
        string memory _description
    ) public onlyAuthorizedRecorder(_shipmentId) shipmentExists(_shipmentId) {
        ShipmentEvent memory newEvent = ShipmentEvent({
            eventType: _eventType,
            location: _location,
            description: _description,
            timestamp: block.timestamp,
            recordedBy: msg.sender,
            verified: false
        });

        shipmentEvents[_shipmentId].push(newEvent);

        emit EventRecorded(
            _shipmentId,
            _eventType,
            _location,
            msg.sender,
            block.timestamp
        );
    }

    /**
     * @dev Update shipment status
     * @param _shipmentId Shipment identifier
     * @param _newStatus New shipment status
     */
    function updateStatus(
        string memory _shipmentId,
        ShipmentStatus _newStatus
    ) public onlyAuthorizedRecorder(_shipmentId) shipmentExists(_shipmentId) {
        require(
            uint(_newStatus) > uint(shipments[_shipmentId].status),
            "Cannot downgrade status"
        );

        shipments[_shipmentId].status = _newStatus;

        emit StatusUpdated(_shipmentId, _newStatus, block.timestamp);
    }

    /**
     * @dev Authorize a party to record events
     * @param _shipmentId Shipment identifier
     * @param _recorder Address to authorize
     */
    function authorizeRecorder(string memory _shipmentId, address _recorder)
        public
        onlyShipper(_shipmentId)
        shipmentExists(_shipmentId)
    {
        require(_recorder != address(0), "Invalid recorder address");

        authorizedRecorders[_shipmentId][_recorder] = true;

        emit RecorderAuthorized(_shipmentId, _recorder, block.timestamp);
    }

    /**
     * @dev Verify an event (e.g., by customs or port authority)
     * @param _shipmentId Shipment identifier
     * @param _eventIndex Index of the event to verify
     */
    function verifyEvent(string memory _shipmentId, uint256 _eventIndex)
        public
        onlyAuthorizedRecorder(_shipmentId)
        shipmentExists(_shipmentId)
    {
        require(_eventIndex < shipmentEvents[_shipmentId].length, "Invalid event index");

        shipmentEvents[_shipmentId][_eventIndex].verified = true;

        emit EventVerified(_shipmentId, _eventIndex, msg.sender, block.timestamp);
    }

    /**
     * @dev Get shipment details
     * @param _shipmentId Shipment identifier
     * @return Shipment struct
     */
    function getShipment(string memory _shipmentId)
        public
        view
        shipmentExists(_shipmentId)
        returns (Shipment memory)
    {
        return shipments[_shipmentId];
    }

    /**
     * @dev Get all events for a shipment
     * @param _shipmentId Shipment identifier
     * @return Array of ShipmentEvent
     */
    function getShipmentEvents(string memory _shipmentId)
        public
        view
        shipmentExists(_shipmentId)
        returns (ShipmentEvent[] memory)
    {
        return shipmentEvents[_shipmentId];
    }

    /**
     * @dev Get event count for a shipment
     * @param _shipmentId Shipment identifier
     * @return uint256 Number of events
     */
    function getEventCount(string memory _shipmentId)
        public
        view
        shipmentExists(_shipmentId)
        returns (uint256)
    {
        return shipmentEvents[_shipmentId].length;
    }

    /**
     * @dev Check if address is authorized recorder
     * @param _shipmentId Shipment identifier
     * @param _recorder Address to check
     * @return bool True if authorized
     */
    function isAuthorizedRecorder(string memory _shipmentId, address _recorder)
        public
        view
        shipmentExists(_shipmentId)
        returns (bool)
    {
        return authorizedRecorders[_shipmentId][_recorder];
    }

    /**
     * @dev Get current shipment status
     * @param _shipmentId Shipment identifier
     * @return ShipmentStatus Current status
     */
    function getStatus(string memory _shipmentId)
        public
        view
        shipmentExists(_shipmentId)
        returns (ShipmentStatus)
    {
        return shipments[_shipmentId].status;
    }

    /**
     * @dev Get total number of shipments
     * @return uint256 Total count
     */
    function getTotalShipments() public view returns (uint256) {
        return shipmentIds.length;
    }

    /**
     * @dev Get the latest event for a shipment
     * @param _shipmentId Shipment identifier
     * @return ShipmentEvent Latest event or empty struct if no events
     */
    function getLatestEvent(string memory _shipmentId)
        public
        view
        shipmentExists(_shipmentId)
        returns (ShipmentEvent memory)
    {
        uint256 eventCount = shipmentEvents[_shipmentId].length;
        if (eventCount == 0) {
            return ShipmentEvent("", "", "", 0, address(0), false);
        }
        return shipmentEvents[_shipmentId][eventCount - 1];
    }
}

client/src/components/BillOfLadingForm.t.content:
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useState } from "react";

interface BillOfLadingFormProps {
  instructionsData?: any;
  onDraft?: (data: BillOfLadingData) => void;
  onFinalize?: (data: BillOfLadingData) => void;
}

export interface BillOfLadingData {
  bolNumber: string;
  vesselName: string;
  voyageNumber: string;
  shipperName: string;
  consigneeName: string;
  notifyParty: string;
  origin: string;
  destination: string;
  containerNumber: string;
  sealNumber: string;
  packageCount: string;
  weight: string;
  cargoDescription: string;
  freightTerms: string;
}

export default function BillOfLadingForm({ instructionsData, onDraft, onFinalize }: BillOfLadingFormProps) {
  const [formData, setFormData] = useState<BillOfLadingData>({
    bolNumber: `BOL-2024-${Math.floor(Math.random() * 1000).toString().padStart(3, '0')}`,
    vesselName: '',
    voyageNumber: '',
    shipperName: instructionsData?.shipperName || '',
    consigneeName: instructionsData?.consigneeName || '',
    notifyParty: '',
    origin: instructionsData?.origin || '',
    destination: instructionsData?.destination || '',
    containerNumber: instructionsData?.containerNumber || '',
    sealNumber: '',
    packageCount: '',
    weight: instructionsData?.weight || '',
    cargoDescription: instructionsData?.cargoDescription || '',
    freightTerms: 'prepaid',
  });

  const handleDraft = () => {
    console.log('Bill of Lading draft saved:', formData);
    onDraft?.(formData);
  };

  const handleFinalize = () => {
    console.log('Bill of Lading finalized:', formData);
    onFinalize?.(formData);
  };

  return (
    <Card data-testid="card-bol-form">
      <CardHeader>
        <CardTitle>Draft Bill of Lading</CardTitle>
        <CardDescription>
          Create a Bill of Lading based on shipping instructions. All changes are recorded on the blockchain.
        </CardDescription>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="space-y-2">
              <Label htmlFor="bolNumber">BoL Number</Label>
              <Input
                id="bolNumber"
                value={formData.bolNumber}
                onChange={(e) => setFormData({ ...formData, bolNumber: e.target.value })}
                className="font-mono"
                data-testid="input-bol-number"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="vesselName">Vessel Name</Label>
              <Input
                id="vesselName"
                placeholder="e.g., MSC Oscar"
                value={formData.vesselName}
                onChange={(e) => setFormData({ ...formData, vesselName: e.target.value })}
                data-testid="input-vessel-name"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="voyageNumber">Voyage Number</Label>
              <Input
                id="voyageNumber"
                placeholder="e.g., V001"
                value={formData.voyageNumber}
                onChange={(e) => setFormData({ ...formData, voyageNumber: e.target.value })}
                data-testid="input-voyage-number"
              />
            </div>
          </div>

          <div className="space-y-4 p-4 border border-border rounded-md">
            <h3 className="font-semibold text-sm">Party Details</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="space-y-2">
                <Label htmlFor="shipperName">Shipper</Label>
                <Input
                  id="shipperName"
                  value={formData.shipperName}
                  onChange={(e) => setFormData({ ...formData, shipperName: e.target.value })}
                  data-testid="input-bol-shipper"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="consigneeName">Consignee</Label>
                <Input
                  id="consigneeName"
                  value={formData.consigneeName}
                  onChange={(e) => setFormData({ ...formData, consigneeName: e.target.value })}
                  data-testid="input-bol-consignee"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="notifyParty">Notify Party</Label>
                <Input
                  id="notifyParty"
                  placeholder="Party to notify"
                  value={formData.notifyParty}
                  onChange={(e) => setFormData({ ...formData, notifyParty: e.target.value })}
                  data-testid="input-notify-party"
                />
              </div>
            </div>
          </div>

          <div className="space-y-4 p-4 border border-border rounded-md">
            <h3 className="font-semibold text-sm">Route & Container</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="origin">Port of Loading</Label>
                <Input
                  id="origin"
                  value={formData.origin}
                  onChange={(e) => setFormData({ ...formData, origin: e.target.value })}
                  data-testid="input-bol-origin"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="destination">Port of Discharge</Label>
                <Input
                  id="destination"
                  value={formData.destination}
                  onChange={(e) => setFormData({ ...formData, destination: e.target.value })}
                  data-testid="input-bol-destination"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="containerNumber">Container Number</Label>
                <Input
                  id="containerNumber"
                  value={formData.containerNumber}
                  onChange={(e) => setFormData({ ...formData, containerNumber: e.target.value })}
                  className="font-mono"
                  data-testid="input-bol-container"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="sealNumber">Seal Number</Label>
                <Input
                  id="sealNumber"
                  placeholder="e.g., SL123456"
                  value={formData.sealNumber}
                  onChange={(e) => setFormData({ ...formData, sealNumber: e.target.value })}
                  data-testid="input-seal-number"
                />
              </div>
            </div>
          </div>

          <div className="space-y-4 p-4 border border-border rounded-md">
            <h3 className="font-semibold text-sm">Cargo Information</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="space-y-2">
                <Label htmlFor="packageCount">Number of Packages</Label>
                <Input
                  id="packageCount"
                  type="number"
                  placeholder="e.g., 100"
                  value={formData.packageCount}
                  onChange={(e) => setFormData({ ...formData, packageCount: e.target.value })}
                  data-testid="input-package-count"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="weight">Gross Weight (kg)</Label>
                <Input
                  id="weight"
                  type="number"
                  value={formData.weight}
                  onChange={(e) => setFormData({ ...formData, weight: e.target.value })}
                  data-testid="input-bol-weight"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="freightTerms">Freight Terms</Label>
                <Select 
                  value={formData.freightTerms} 
                  onValueChange={(value) => setFormData({ ...formData, freightTerms: value })}
                >
                  <SelectTrigger id="freightTerms" data-testid="select-freight-terms">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="prepaid">Prepaid</SelectItem>
                    <SelectItem value="collect">Collect</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
            <div className="space-y-2">
              <Label htmlFor="cargoDescription">Cargo Description</Label>
              <Textarea
                id="cargoDescription"
                value={formData.cargoDescription}
                onChange={(e) => setFormData({ ...formData, cargoDescription: e.target.value })}
                className="min-h-[80px]"
                data-testid="input-bol-cargo-description"
              />
            </div>
          </div>

          <div className="flex gap-3">
            <Button 
              type="button" 
              variant="outline" 
              className="flex-1"
              onClick={handleDraft}
              data-testid="button-draft-bol"
            >
              Save Draft
            </Button>
            <Button 
              type="button" 
              className="flex-1"
              onClick={handleFinalize}
              data-testid="button-finalize-bol"
            >
              Finalize & Share
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

client/src/components/ui/alert-dialog.ts.content:
import * as React from "react"
import * as AlertDialogPrimitive from "@radix-ui/react-alert-dialog"

import { cn } from "@/lib/utils"
import { buttonVariants } from "@/components/ui/button"

const AlertDialog = AlertDialogPrimitive.Root

const AlertDialogTrigger = AlertDialogPrimitive.Trigger

const AlertDialogPortal = AlertDialogPrimitive.Portal

const AlertDialogOverlay = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Overlay
    className={cn(
      "fixed inset-0 z-50 bg-black/80  data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0",
      className
    )}
    {...props}
    ref={ref}
  />
))
AlertDialogOverlay.displayName = AlertDialogPrimitive.Overlay.displayName

const AlertDialogContent = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Content>
>(({ className, ...props }, ref) => (
  <AlertDialogPortal>
    <AlertDialogOverlay />
    <AlertDialogPrimitive.Content
      ref={ref}
      className={cn(
        "fixed left-[50%] top-[50%] z-50 grid w-full max-w-lg translate-x-[-50%] translate-y-[-50%] gap-4 border bg-background p-6 shadow-lg duration-200 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%] data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%] sm:rounded-lg",
        className
      )}
      {...props}
    />
  </AlertDialogPortal>
))
AlertDialogContent.displayName = AlertDialogPrimitive.Content.displayName

const AlertDialogHeader = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col space-y-2 text-center sm:text-left",
      className
    )}
    {...props}
  />
)
AlertDialogHeader.displayName = "AlertDialogHeader"

const AlertDialogFooter = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col-reverse sm:flex-row sm:justify-end sm:space-x-2",
      className
    )}
    {...props}
  />
)
AlertDialogFooter.displayName = "AlertDialogFooter"

const AlertDialogTitle = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Title>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Title>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Title
    ref={ref}
    className={cn("text-lg font-semibold", className)}
    {...props}
  />
))
AlertDialogTitle.displayName = AlertDialogPrimitive.Title.displayName

const AlertDialogDescription = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Description>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Description>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Description
    ref={ref}
    className={cn("text-sm text-muted-foreground", className)}
    {...props}
  />
))
AlertDialogDescription.displayName =
  AlertDialogPrimitive.Description.displayName

const AlertDialogAction = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Action>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Action>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Action
    ref={ref}
    className={cn(buttonVariants(), className)}
    {...props}
  />
))
AlertDialogAction.displayName = AlertDialogPrimitive.Action.displayName

const AlertDialogCancel = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Cancel>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Cancel>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Cancel
    ref={ref}
    className={cn(
      buttonVariants({ variant: "outline" }),
      "mt-2 sm:mt-0",
      className
    )}
    {...props}
  />
))
AlertDialogCancel.displayName = AlertDialogPrimitive.Cancel.displayName

export {
  AlertDialog,
  AlertDialogPortal,
  AlertDialogOverlay,
  AlertDialogTrigger,
  AlertDialogContent,
  AlertDialogHeader,
  AlertDialogFooter,
  AlertDialogTitle,
  AlertDialogDescription,
  AlertDialogAction,
  AlertDialogCancel,
}

