  const mockDocuments = [
    {
      id: '1',
      bolNumber: 'BOL-2024-001',
      shipperName: 'Global Logistics Co.',
      status: 'in_transit' as const,
      date: 'Jan 10, 2024',
      isBlockchainVerified: true,
    },
    {
      id: '2',
      bolNumber: 'BOL-2024-002',
      shipperName: 'Ocean Freight Ltd.',
