import { useState } from 'react';
import { Card, CardContent, CardHeader } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { FileText, Ship, Package, MapPin, Save, Send, Loader2 } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useMutation } from '@tanstack/react-query';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { useLocation } from 'wouter';

export default function CreateBillOfLadingForm() {
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const [formData, setFormData] = useState({
    blNumber: '',
    shipper: '',
    consignee: '',
    notifyParty: '',
    vesselName: '',
    voyageNumber: '',
    portOfLoading: '',
    portOfDischarge: '',
    cargoDescription: '',
    containerNumbers: '',
    grossWeight: '',
    numberOfPackages: '',
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const createBolMutation = useMutation({
    mutationFn: async (data: typeof formData & { status: string }) => {
      const res = await apiRequest('POST', '/api/bills-of-lading', {
        blNumber: data.blNumber,
        shipper: data.shipper,
        consignee: data.consignee,
        notifyParty: data.notifyParty || null,
        vesselName: data.vesselName,
        voyageNumber: data.voyageNumber,
        portOfLoading: data.portOfLoading,
        portOfDischarge: data.portOfDischarge,
        cargoDescription: data.cargoDescription,
        containerNumbers: data.containerNumbers || null,
        numberOfPackages: data.numberOfPackages ? parseInt(data.numberOfPackages) : null,
        grossWeight: data.grossWeight || null,
        status: data.status,
      });
      return await res.json();
    },
    onSuccess: (data: any) => {
      queryClient.invalidateQueries({ queryKey: ['/api/bills-of-lading'] });
      toast({
        title: 'Success',
        description: `Bill of Lading ${data.blNumber} created successfully`,
      });
      setLocation('/documents');
    },
    onError: (error: Error) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to create Bill of Lading',
        variant: 'destructive',
      });
    },
  });

  const handleSaveDraft = (e: React.FormEvent) => {
    e.preventDefault();
    createBolMutation.mutate({ ...formData, status: 'draft' });
  };

  const handleSubmitToBlockchain = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.blNumber || !formData.shipper || !formData.consignee || 
        !formData.vesselName || !formData.voyageNumber || !formData.portOfLoading || 
        !formData.portOfDischarge || !formData.cargoDescription) {
      toast({
        title: 'Validation Error',
        description: 'Please fill in all required fields',
        variant: 'destructive',
      });
      return;
    }
    createBolMutation.mutate({ ...formData, status: 'submitted' });
  };

  return (
    <Card data-testid="form-create-bol">
      <CardHeader>
        <div className="flex items-center gap-2">
          <FileText className="h-5 w-5 text-primary" />
          <h2 className="text-xl font-semibold">Create Bill of Lading</h2>
        </div>
      </CardHeader>
      <CardContent>
        <form className="space-y-6">
          <div className="grid lg:grid-cols-2 gap-4">
            <div>
              <Label htmlFor="blNumber">B/L Number *</Label>
              <Input
                id="blNumber"
                name="blNumber"
                value={formData.blNumber}
                onChange={handleChange}
                placeholder="BL-2024-XXX"
                required
                data-testid="input-bl-number"
              />
            </div>
            <div>
              <Label htmlFor="voyageNumber">Voyage Number *</Label>
              <Input
                id="voyageNumber"
                name="voyageNumber"
                value={formData.voyageNumber}
                onChange={handleChange}
                placeholder="V425E"
                required
                data-testid="input-voyage-number"
              />
            </div>
          </div>

          <div className="space-y-2">
            <div className="flex items-center gap-2 text-sm font-semibold text-muted-foreground">
              <Package className="h-4 w-4" />
              <span>Parties Information</span>
            </div>
            <div className="grid lg:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="shipper">Shipper *</Label>
                <Input
                  id="shipper"
                  name="shipper"
                  value={formData.shipper}
                  onChange={handleChange}
                  placeholder="Company Name & Address"
                  required
                  data-testid="input-shipper"
                />
              </div>
              <div>
                <Label htmlFor="consignee">Consignee *</Label>
                <Input
                  id="consignee"
                  name="consignee"
                  value={formData.consignee}
                  onChange={handleChange}
                  placeholder="Company Name & Address"
                  required
                  data-testid="input-consignee"
                />
              </div>
              <div className="lg:col-span-2">
                <Label htmlFor="notifyParty">Notify Party</Label>
                <Input
                  id="notifyParty"
                  name="notifyParty"
                  value={formData.notifyParty}
                  onChange={handleChange}
                  placeholder="Company Name & Address"
                  data-testid="input-notify-party"
                />
              </div>
            </div>
          </div>

          <div className="space-y-2">
            <div className="flex items-center gap-2 text-sm font-semibold text-muted-foreground">
              <Ship className="h-4 w-4" />
              <span>Vessel & Route</span>
            </div>
            <div className="grid lg:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="vesselName">Vessel Name *</Label>
                <Input
                  id="vesselName"
                  name="vesselName"
                  value={formData.vesselName}
                  onChange={handleChange}
                  placeholder="MSC GÜLSÜN"
                  required
                  data-testid="input-vessel-name"
                />
              </div>
              <div>
                <Label htmlFor="portOfLoading">Port of Loading *</Label>
                <Input
                  id="portOfLoading"
                  name="portOfLoading"
                  value={formData.portOfLoading}
                  onChange={handleChange}
                  placeholder="Shanghai, China"
                  required
                  data-testid="input-port-loading"
                />
              </div>
              <div className="lg:col-span-2">
                <Label htmlFor="portOfDischarge">Port of Discharge *</Label>
                <Input
                  id="portOfDischarge"
                  name="portOfDischarge"
                  value={formData.portOfDischarge}
                  onChange={handleChange}
                  placeholder="Rotterdam, Netherlands"
                  required
                  data-testid="input-port-discharge"
                />
              </div>
            </div>
          </div>

          <div className="space-y-2">
            <div className="flex items-center gap-2 text-sm font-semibold text-muted-foreground">
              <MapPin className="h-4 w-4" />
              <span>Cargo Details</span>
            </div>
            <div className="space-y-4">
              <div>
                <Label htmlFor="cargoDescription">Cargo Description *</Label>
                <Textarea
                  id="cargoDescription"
                  name="cargoDescription"
                  value={formData.cargoDescription}
                  onChange={handleChange}
                  placeholder="Electronic Components - 500 cartons"
                  rows={3}
                  required
                  data-testid="input-cargo-description"
                />
              </div>
              <div className="grid lg:grid-cols-3 gap-4">
                <div>
                  <Label htmlFor="containerNumbers">Container Numbers</Label>
                  <Input
                    id="containerNumbers"
                    name="containerNumbers"
                    value={formData.containerNumbers}
                    onChange={handleChange}
                    placeholder="MSCU1234567, MSCU7654321"
                    data-testid="input-container-numbers"
                  />
                </div>
                <div>
                  <Label htmlFor="numberOfPackages">Number of Packages</Label>
                  <Input
                    id="numberOfPackages"
                    name="numberOfPackages"
                    type="number"
                    value={formData.numberOfPackages}
                    onChange={handleChange}
                    placeholder="500"
                    data-testid="input-packages"
                  />
                </div>
                <div>
                  <Label htmlFor="grossWeight">Gross Weight (kg)</Label>
                  <Input
                    id="grossWeight"
                    name="grossWeight"
                    value={formData.grossWeight}
                    onChange={handleChange}
                    placeholder="12500"
                    data-testid="input-weight"
                  />
                </div>
              </div>
            </div>
          </div>

          <div className="flex gap-3 pt-4 border-t">
            <Button 
              type="button" 
              variant="outline" 
              className="gap-2"
              onClick={handleSaveDraft}
              disabled={createBolMutation.isPending}
              data-testid="button-save-draft"
            >
              {createBolMutation.isPending ? (
                <Loader2 className="h-4 w-4 animate-spin" />
              ) : (
                <Save className="h-4 w-4" />
              )}
              Save Draft
            </Button>
            <Button 
              type="button" 
              variant="default" 
              className="gap-2"
              onClick={handleSubmitToBlockchain}
              disabled={createBolMutation.isPending}
              data-testid="button-submit-blockchain"
            >
              {createBolMutation.isPending ? (
                <Loader2 className="h-4 w-4 animate-spin" />
              ) : (
                <Send className="h-4 w-4" />
              )}
              Submit to Blockchain
            </Button>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

client/src/components/ui/chart.tsx.content:
"use client"

import * as React from "react"
import * as RechartsPrimitive from "recharts"

import { cn } from "@/lib/utils"

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: "", dark: ".dark" } as const

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode
    icon?: React.ComponentType
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  )
}

type ChartContextProps = {
  config: ChartConfig
}

const ChartContext = React.createContext<ChartContextProps | null>(null)

function useChart() {
  const context = React.useContext(ChartContext)

  if (!context) {
    throw new Error("useChart must be used within a <ChartContainer />")
  }

  return context
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    config: ChartConfig
    children: React.ComponentProps<
      typeof RechartsPrimitive.ResponsiveContainer
    >["children"]
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId()
  const chartId = `chart-${id || uniqueId.replace(/:/g, "")}`

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>
          {children}
        </RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  )
})
ChartContainer.displayName = "Chart"

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(
    ([, config]) => config.theme || config.color
  )

  if (!colorConfig.length) {
    return null
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color =
      itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ||
      itemConfig.color
    return color ? `  --color-${key}: ${color};` : null
  })
  .join("\n")}
}
`
          )
          .join("\n"),
      }}
    />
  )
}

const ChartTooltip = RechartsPrimitive.Tooltip

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<"div"> & {
      hideLabel?: boolean
      hideIndicator?: boolean
      indicator?: "line" | "dot" | "dashed"
      nameKey?: string
      labelKey?: string
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = "dot",
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref
  ) => {
    const { config } = useChart()

    const tooltipLabel = React.useMemo(() => {
      if (hideLabel || !payload?.length) {
        return null
      }

      const [item] = payload
      const key = `${labelKey || item?.dataKey || item?.name || "value"}`
      const itemConfig = getPayloadConfigFromPayload(config, item, key)
      const value =
        !labelKey && typeof label === "string"
          ? config[label as keyof typeof config]?.label || label
          : itemConfig?.label

      if (labelFormatter) {
        return (
          <div className={cn("font-medium", labelClassName)}>
            {labelFormatter(value, payload)}
          </div>
        )
      }

      if (!value) {
        return null
      }

      return <div className={cn("font-medium", labelClassName)}>{value}</div>
    }, [
      label,
      labelFormatter,
      payload,
      hideLabel,
      labelClassName,
      config,
      labelKey,
    ])

    if (!active || !payload?.length) {
      return null
    }

    const nestLabel = payload.length === 1 && indicator !== "dot"

    return (
      <div
        ref={ref}
        className={cn(
          "grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl",
          className
        )}
      >
        {!nestLabel ? tooltipLabel : null}
        <div className="grid gap-1.5">
          {payload.map((item, index) => {
            const key = `${nameKey || item.name || item.dataKey || "value"}`
            const itemConfig = getPayloadConfigFromPayload(config, item, key)
            const indicatorColor = color || item.payload.fill || item.color

            return (
              <div
                key={item.dataKey}
                className={cn(
                  "flex w-full flex-wrap items-stretch gap-2 [&>svg]:h-2.5 [&>svg]:w-2.5 [&>svg]:text-muted-foreground",
                  indicator === "dot" && "items-center"
                )}
              >
                {formatter && item?.value !== undefined && item.name ? (
                  formatter(item.value, item.name, item, index, item.payload)
                ) : (
                  <>
                    {itemConfig?.icon ? (
                      <itemConfig.icon />
                    ) : (
                      !hideIndicator && (
                        <div
                          className={cn(
                            "shrink-0 rounded-[2px] border-[--color-border] bg-[--color-bg]",
                            {
                              "h-2.5 w-2.5": indicator === "dot",
                              "w-1": indicator === "line",
                              "w-0 border-[1.5px] border-dashed bg-transparent":
                                indicator === "dashed",
                              "my-0.5": nestLabel && indicator === "dashed",
                            }
                          )}
                          style={
                            {
                              "--color-bg": indicatorColor,
                              "--color-border": indicatorColor,
                            } as React.CSSProperties
                          }
                        />
                      )
                    )}
                    <div
                      className={cn(
                        "flex flex-1 justify-between leading-none",
                        nestLabel ? "items-end" : "items-center"
                      )}
                    >
                      <div className="grid gap-1.5">
                        {nestLabel ? tooltipLabel : null}
                        <span className="text-muted-foreground">
                          {itemConfig?.label || item.name}
                        </span>
                      </div>
                      {item.value && (
                        <span className="font-mono font-medium tabular-nums text-foreground">
                          {item.value.toLocaleString()}
                        </span>
                      )}
                    </div>
                  </>
                )}
              </div>
            )
          })}
        </div>
      </div>
    )
  }
)
ChartTooltipContent.displayName = "ChartTooltip"

const ChartLegend = RechartsPrimitive.Legend

const ChartLegendContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> &
    Pick<RechartsPrimitive.LegendProps, "payload" | "verticalAlign"> & {
      hideIcon?: boolean
      nameKey?: string
    }
>(
  (
    { className, hideIcon = false, payload, verticalAlign = "bottom", nameKey },
    ref
  ) => {
    const { config } = useChart()

    if (!payload?.length) {
      return null
    }

    return (
      <div
        ref={ref}
        className={cn(
          "flex items-center justify-center gap-4",
          verticalAlign === "top" ? "pb-3" : "pt-3",
          className
        )}
      >
        {payload.map((item) => {
          const key = `${nameKey || item.dataKey || "value"}`
          const itemConfig = getPayloadConfigFromPayload(config, item, key)

          return (
            <div
              key={item.value}
              className={cn(
                "flex items-center gap-1.5 [&>svg]:h-3 [&>svg]:w-3 [&>svg]:text-muted-foreground"
              )}
            >
              {itemConfig?.icon && !hideIcon ? (
                <itemConfig.icon />
              ) : (
                <div
                  className="h-2 w-2 shrink-0 rounded-[2px]"
                  style={{
                    backgroundColor: item.color,
                  }}
                />
              )}
              {itemConfig?.label}
            </div>
          )
        })}
      </div>
    )
  }
)
ChartLegendContent.displayName = "ChartLegend"

// Helper to extract item config from a payload.
function getPayloadConfigFromPayload(
  config: ChartConfig,
  payload: unknown,
  key: string
) {
  if (typeof payload !== "object" || payload === null) {
    return undefined
  }

  const payloadPayload =
    "payload" in payload &&
    typeof payload.payload === "object" &&
    payload.payload !== null
      ? payload.payload
      : undefined

  let configLabelKey: string = key

  if (
    key in payload &&
    typeof payload[key as keyof typeof payload] === "string"
  ) {
    configLabelKey = payload[key as keyof typeof payload] as string
  } else if (
    payloadPayload &&
    key in payloadPayload &&
    typeof payloadPayload[key as keyof typeof payloadPayload] === "string"
  ) {
    configLabelKey = payloadPayload[
      key as keyof typeof payloadPayload
    ] as string
  }

  return configLabelKey in config
    ? config[configLabelKey]
    : config[key as keyof typeof config]
}

export {
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
  ChartLegend,
  ChartLegendContent,
  ChartStyle,
}

